mapper.select:
Select
count(*) from member
where uId = #{uId}

Select
count(*) from member
where uId = #{uId} and uPw = #{uPw}

mapper.insert:
Insert into member (uId, uPw, uName, uPhone, writer,
uEmail,
uBirth
) Values (#{uId}, #{uPw}, #{uName}, #{uPhone},
#{uId},
#{writer},
#{uEmail},
#{uBirth}
)

mapper.select:
Select uId
from member
where uName = #{uName} and uPhone = #{uPhone}

Select
count(*) from member
where uId = #{uId} and uPhone = #{uPhone}
